    public static AtomicInteger selected = new AtomicInteger(0);

    private Container container;
    private JLabel selector;

    public AppMenuWindow() {
        container = this.getContentPane();
        container.setLayout(null);
        container.setBackground(new Color(40, 40, 40));

        for (int i = 0; i < appNames.length; ++i) {
            JLabel icon = new JLabel();
            icon.setIcon(getImageIconFromBase64String(appIcons[i]));
            icon.setBounds(margin + (iconSize + margin) * (i % gridMaxX), margin + (iconSize + margin) * (i / gridMaxX), iconSize, iconSize);
            container.add(icon);
        }

        JLabel labelSel = new JLabel();
        labelSel.setIcon(getImageIconFromBase64String(selIcon));
        labelSel.setBounds(margin / 2, margin / 2, selSize, selSize);
        labelSel.setVisible(false);
        container.add(labelSel);
        selector = labelSel;

        this.setSize(margin + (iconSize + margin) * gridMaxX,
                margin + (iconSize + margin) * (appNames.length / gridMaxX + (appNames.length % gridMaxX == 0 ? 0 : 1)));
        this.getRootPane().putClientProperty("Window.alpha", 0.75f);
        this.setResizable(false);
        this.setAlwaysOnTop(true);
        this.setUndecorated(true);
        this.setVisible(false);
//        this.setVisible(true);
    }

    private static ImageIcon getImageIconFromBase64String(String base64String) {
        byte[] bytes = Base64.getDecoder().decode(base64String);

        BufferedImage bufferedImage = null;
        InputStream inputStream = null;
        try {
            inputStream = new ByteArrayInputStream(bytes);
            bufferedImage = ImageIO.read(inputStream);
        } catch (IOException e) {
            e.printStackTrace();
        }
        if (inputStream != null) {
            try {
                inputStream.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
            inputStream = null;
        }
        return new ImageIcon(bufferedImage);
    }

    public void moveSelector(int mouseX, int mouseY) {
        int minX = this.getX() + margin / 2;
        int maxX = this.getX() + this.getWidth() - margin / 2;
        int minY = this.getY() + margin / 2;
        int maxY = this.getY() + this.getHeight() - margin / 2;
        if (mouseX <= minX || mouseX >= maxX || mouseY <= minY || mouseY >= maxY) {
            selector.setVisible(false);
            selected.set(0);
        } else {
            selector.setVisible(true);
            int x = (mouseX - minX) / selSize;
            if (x == maxX) {
                --x;
            }
            int y = (mouseY - minY) / selSize;
            if (y == (appNames.length / gridMaxX + (appNames.length % gridMaxX == 0 ? 0 : 1))) {
                --y;
            }
            int selectedIndex = y * gridMaxX + x + 1;
            if (selectedIndex > appNames.length) {
                selectedIndex = 0;
                selector.setVisible(false);
            } else {
                selector.setLocation(margin / 2 + selSize * x, margin / 2 + selSize * y);
                selector.setVisible(true);
            }
            selected.set(selectedIndex);
        }
    }

    public void launchApp() {
        if (selected.get() == 0) {
            return;
        }
        try {
            for (int i = 0; i < cmds[selected.get() - 1].length; ++i) {
                Runtime.getRuntime().exec(cmds[selected.get() - 1][i]);
            }
        } catch (Exception e) {
        }
    }
